import { useState, useEffect } from "react";
import { ethers } from "ethers";

const Contributers = ({ state }) => {
  const [raisingAmt, setRaisingAmt] = useState(0);
  const [raisedAmt, setRaisedAmt] = useState(0);
  const [amtLeft, setAmtLeft] = useState(0);
  const { contract } = state;

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (contract) {
          const raisingAmount = await contract.raisingAmt();
          const raisedAmount = await contract.raisedAmt();

          setRaisingAmt(raisingAmount.toNumber());
          setRaisedAmt(raisedAmount.toNumber());

          const left = raisingAmount.sub(raisedAmount);
          setAmtLeft(left.toNumber());

          // Listen for contract events for changes in raised amount
          contract.on("DonationAdded", async (contributor, amount) => {
            const updatedRaisedAmount = await contract.raisedAmt();
            setRaisedAmt(updatedRaisedAmount.toNumber());

            const updatedLeft = raisingAmount.sub(updatedRaisedAmount);
            setAmtLeft(updatedLeft.toNumber());
          });
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    return () => {
      // Remove the event listener when component unmounts
      if (contract) {
        contract.removeAllListeners("DonationAdded");
      }
    };
  }, [contract]);

  return (
    <div className="contri-container">
      <h2>Raising Amount: {raisingAmt} Ether</h2>
      <h2>Raised Amount: {raisedAmt} Ether</h2>
      <h2>Amount Left: {amtLeft} Ether</h2>
    </div>
  );
};

export default Contributers;
