import { useState, useEffect } from "react";
import { ethers } from "ethers";

const Contributers = ({ state }) => {
  const [raisingAmt, setRaisingAmt] = useState(0); // State to hold the raising amount
  const [raisedAmt, setRaisedAmt] = useState(); // State to hold the raised amount
  const { contract } = state;

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (contract) {
          const raisingAmount = await contract.raisingAmt();
          setRaisingAmt(raisingAmount.toNumber()); // Convert Big Number to number

          const raisedAmount = await contract.raisedAmt();
          setRaisedAmt(raisedAmount.toNumber()); // Convert Big Number to number
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [contract]);

  return (
    <div className="contri-container">
      <h2>Raising Amount: {raisingAmt} Ether</h2>
      <h2>Raised Amount: {raisedAmt} Ether</h2>
    </div>
  );
};

export default Contributers;
