import { ethers } from "ethers";
import { useState } from "react";

const Buy = ({ state }) => {
  const [raisedAmount, setRaisedAmount] = useState(0);

  const sendDonation = async (event) => {
    event.preventDefault();
    const { contract } = state;
    const name = document.querySelector("#name").value;
    console.log(name, contract);
    const donationAmount = ethers.utils.parseEther("0.001");
    const amount = { value: donationAmount };

    const transaction = await contract.sendDonation(name, amount);
    await transaction.wait();
    alert("Transaction done");
    setRaisedAmount((prevAmount) => prevAmount + 0.001);
  };

  return (
    <>
      <div className="App-button-box">
        <p>Raised Amount: {raisedAmount}</p>
        <form onSubmit={sendDonation}>
          <div className="mb-3">
            <label className="form-label">Name</label>
            <input
              type="text"
              id="name"
              placeholder="Enter Your Name"
            />
          </div>

          <button type="submit" disabled={!state.contract}>
            Pay
          </button>
        </form>
      </div>
    </>
  );
};

export default Buy;
