import { ethers } from "ethers";
import { useState, useEffect } from "react";

const Buy = ({ state }) => {
  const [transactionInProgress, setTransactionInProgress] = useState(false);
  const [transactionError, setTransactionError] = useState(null);

  const sendDonation = async (event) => {
    event.preventDefault();
    try {
      setTransactionInProgress(true);

      const { contract } = state;
      const name = document.querySelector("#name").value;
      const amount = { value: ethers.utils.parseEther("0.001") };

      // Send donation transaction
      const transaction = await contract.sendDonation(name, amount);
      await transaction.wait();
      alert("Transaction done");

      // Fetch updated raised amount after successful transaction
      const updatedRaisedAmount = await contract.raisedAmt();
      console.log("Updated Raised Amount:", updatedRaisedAmount.toString());
      // You can update the UI with the new raised amount here
    } catch (error) {
      setTransactionError(error.message || "Transaction failed");
      console.error("Transaction failed:", error);
    } finally {
      setTransactionInProgress(false);
    }
  };

  return (
    <>
      <div className="App-button-box">
        <form onSubmit={sendDonation}>
          <div className="mb-3">
            <label className="form-label">Name</label>
            <input type="text" id="name" placeholder="Enter Your Name" />
          </div>

          <button type="submit" disabled={transactionInProgress || !state.contract}>
            {transactionInProgress ? "Processing..." : "Pay"}
          </button>
          {transactionError && <p style={{ color: "red" }}>{transactionError}</p>}
        </form>
      </div>
    </>
  );
};

export default Buy;
