import { useState, useEffect } from "react";

const Amount = ({ state }) => {
  const [raisingAmt, setRaisingAmt] = useState(30);
  const [raisedAmt, setRaisedAmt] = useState(0);
  const { contract } = state;

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (contract) {
          const amount = await contract.displayAmount();
          setRaisedAmt(amount.toNumber());

          // Listen to the contract event for donation added
          contract.on("DonationAdded", async (donor, amount) => {
            const updatedRaisedAmount = await contract.displayAmount();
            setRaisedAmt(updatedRaisedAmount.toNumber());
          });
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();

    // Remove event listener when component unmounts
    return () => {
      if (contract) {
        contract.removeAllListeners("DonationAdded");
      }
    };
  }, [contract]);

  return (
    <div className="contri-container">
      <h2>Raising Amount: {raisingAmt}</h2>
      <h2>Raised Amount: {raisedAmt}</h2>
    </div>
  );
};

export default Amount;
