import { useState, useEffect } from "react";

const Amount = ({ state }) => {
  const [RaisingAmt, setRaisingAmt] = useState(30);
  const [raisedAmt, setRaisedAmt] = useState(0);
  const { contract } = state;

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (contract) {
          const contributersData = await contract.displayAmount();
          setRaisedAmt(contributersData);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [contract]);


  return (
    <div className="contri-container">
      <h2>Contributers</h2>
      {/* {contributers.map((contributers, index) => (
        <div className="contri" key={index}>
          <p>Name: {contributers.name}</p>
          <p>
            Time: {new Date(contributers.timestamp * 1000).toLocaleString()}
          </p>
          <p>
            From: {contributers.from.slice(0, 4).toUpperCase()}...
            {contributers.from.slice(-4).toUpperCase()}
          </p>
        </div>
      ))} */}
      <h1>{raisedAmt}</h1>
    </div>
  );
};

export default Amount;
